\begin{matrix}
 1    &amp;amp; 0   &amp;amp; 0 &amp;amp; -x_0 \\
 0    &amp;amp; 1 	&amp;amp; 0 &amp;amp; -y_0 \\
 0    &amp;amp; 0   &amp;amp; 1 &amp;amp; -z_0 \\
 0    &amp;amp; 0   &amp;amp; 0 &amp;amp; 1 \\
\end{matrix}
\right]
