\begin{matrix}
1    &amp;amp; 0   &amp;amp; 0 &amp;amp; -(r+l)/2 \\
 0    &amp;amp; 1 	&amp;amp; 0 &amp;amp; -(t+b)/2 \\
 0    &amp;amp; 0   &amp;amp; 1 &amp;amp; -(n+f)/2 \\
 0    &amp;amp; 0   &amp;amp; 0 &amp;amp; 1 \\
 \end{matrix}
\right]
