\left[
\begin{matrix}
 1    &amp;amp; 0     &amp;amp; 0\\
 0    &amp;amp; cosθ  &amp;amp; -sinθ \\
 0    &amp;amp; sinθ &amp;amp; cosθ  \\
\end{matrix}
\right]
\left[
\begin{matrix}
 0   &amp;amp; 0   &amp;amp; 1\\
 0   &amp;amp; 1   &amp;amp; 0 \\
 -1  &amp;amp; 0   &amp;amp; 0 \\
\end{matrix}
\right]=
\left[
\begin{matrix}
 0     &amp;amp; 0     &amp;amp; 1\\
 sinθ  &amp;amp; cosθ  &amp;amp; 0 \\
 -cosθ &amp;amp; sinθ   &amp;amp; 0 \\
\end{matrix}
