n&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let r = imageData.data[i]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let g = imageData.data[i+1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let b = imageData.data[i+2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let avg = .299 * r + .587 * g + .114 * b&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        imageData.data[i] = avg&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        imageData.data[i+1] = avg&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        imageData.data[i+2] = avg&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt; &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    ctx.putImageData(imageData,0,0,0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //ÂõæÁâáÂèçËâ≤Ë∞É&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    invertColor.width = img.width&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    invertColor.height = img.height&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    invertColorctx.drawImage(img,0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    let invertColorData = invertColorctx.getImageData(0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(let i=0;i&amp;lt;invertColorData.data.length;i+=4)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        invertColorData.data[i] = 255-invertColorData.data[i]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        invertColorData.data[i+1] = 255-invertColorData.data[i+1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        invertColorData.data[i+2] = 255-invertColorData.data[i+2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    invertColorctx.putImageData(invertColorData,0,0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //ÂõæÁâáÁÅ∞Ëâ≤Ë∞É&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    adjustColor.width = img.width&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    adjustColor.height = img.height&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    adjustColorctx.drawImage(img,0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    let adjustColorData = adjustColorctx.getImageData(0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(let i=0;i&amp;lt;adjustColorData.data.length;i+=4)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let r = adjustColorData.data[i]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let g = adjustColorData.data[i+1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        let b = adjustColorData.data[i+2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        adjustColorData.data[i] = (r * 0.272) + (g * 0.534) + (b * 0.131)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        adjustColorData.data[i+1] = (r * 0.349) + (g * 0.686) + (b * 0.168)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        adjustColorData.data[i+2] = (r * 0.393) + (g * 0.769) + (b * 0.189)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    adjustColorctx.putImageData(adjustColorData,0,0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    //ÂõæÁâáÊ®°Á≥ä,ËøôÈáåÊòØÁî®3X3ÁöÑÂç∑ÁßØÊ†∏&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    blurImage.width = img.width&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    blurImage.height = img.height&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    blurImagectx.drawImage(img,0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    let blurImageData = blurImagectx.getImageData(0,0,img.width,img.height)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    for(let i=0;i&amp;lt;blurImageData.height-2;i++)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        for(let j=0;j&amp;lt;blurImageData.width*4-8;j+=4)&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            let list1 = [0,1,2],list2 = [0,4,8]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            let tr=0,tg=0,tb=0&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            list1.forEach((a)=&amp;gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                list2.forEach((b)=&amp;gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    let num = j+b+(i+a)*blurImageData.width*4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    tr += blurImageData.data[num]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    tg += blurImageData.data[num+1]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    tb += blurImageData.data[num+2]&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            tr = tr/9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            tg = tg/9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            tb = tb/9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            list1.forEach((a)=&amp;gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                list2.forEach((b)=&amp;gt;&amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    let num = j+b+(i+a)*blurImageData.width*4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    blurImageData.data[num] = tr&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    blurImageData.data[num+1] = tg&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                    blurImageData.data[num+2] = tb&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;                &amp;#125;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;            &amp;#125;)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;        &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    blurImagectx.putImageData(blurImageData,0,0)&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
